class Foe():
    def __init__(self, *, pos=(0,0), name,
                 distance, atk, wpn, shellGlyph, speed, reloadSpeed, health, drop) -> None:
        self.pos=pos
        self.atk=atk
        self.wpn=wpn
        self._drop = drop
        self.name = name
        self.fullName = name
        self._shellGlyph = ttk.TTkString(shellGlyph)
        self.speed = speed
        self.active = False
        self.health = health
        self.distance = distance
        self.picture = Tiles[name]
        self.reloadSpeed = reloadSpeed
        self.time = random.randint(0,10)
        self._info = []

        self.genInfo()

    @property
    def info(self):
        return self._info + [ttk.TTkString(f"Health: {self.health}/{FOELIST[self.name]['health']}")]

    def shellGlyph(self):
        return self._shellGlyph

    def genInfo(self):
        def _getName():
            return stolenNames[random.randint(0,len(stolenNames)-1)]
        self.fullName = _getName()
        self._info = [
            ttk.TTkString(f" {self.picture} {self.fullName} the {self.name} ")]
        r = random.randint(0,20)
        if r == 0: # married 1
            wife = _getName()
            son = _getName()
            nkids = random.randint(2,10)
            self._info += [
                ttk.TTkString(f"he is in love with {wife}"),
                ttk.TTkString(f"a beautiful {self.name}"),
                ttk.TTkString(f"they have {nkids} amazing kids"),
                # ttk.TTkString(f"his son {son} is also his best friend")
            ]
        elif r == 1: # family 2
            wife = _getName()
            son = _getName()
            nkids = random.randint(2,10)
            self._info += [
                ttk.TTkString(f"His son {son} is also his best friend"),
                ttk.TTkString(f"he mean the world to him"),
            ]
        elif r == 2:
            son = _getName()
            self._info += [
                ttk.TTkString(f"she is a single mom"),
                ttk.TTkString(f"her daughter {son} is"),
                ttk.TTkString(f"very smart and lovely"),
            ]
        elif r == 3: # married 1
            self._info += [
                ttk.TTkString(f"Yesterday had the best burrito of his life"),
            ]
        elif r == 4: # married 1
            self._info += [
                ttk.TTkString(f"Every Sunday he helps the neighbourhood"),
                ttk.TTkString(f"cooking meals for the homeless"),
            ]
        elif r == 5: # married 1
            dog = _getName()
            self._info += [
                ttk.TTkString(f"She loves to cuddle her dog {dog}"),
            ]
        else:
            time1 = (
                random.choice(['Every '])+
                random.choice(['Monday','Tuesday','Wednesday','Thursday','Friday','Saturday','Sunday','weekend']))
            time2 = (
                random.choice(['Once a ','Twice a ','Every '])+
                random.choice(['week','month','year']))
            hobby = random.choice([time1,time2]) + random.choice([" He is "," She is "]) + random.choice(kindness_hobbies).lower()
            if len(hobby) > 30:
                hobby = hobby.split(' ')
                hobby = [' '.join(hobby[0:2+len(hobby)//2]),' '.join(hobby[2+len(hobby)//2:])]
            else:
                hobby = [hobby]
            for h in hobby:
                self._info.append(ttk.TTkString(h))

    def drop(self):
        drop = []
        if self._drop:
            for _ in range(random.randint(1,3)):
                drop.append(random.choice(self._drop))
        return drop

    def getActions(self) -> list[bool,bool]: # return move or shot
        self.time += 1
        move = False if not self.speed       else 0 == (self.time)%(10-self.speed)
        shot = False if not self.reloadSpeed else 0 == (self.time)%(10-self.reloadSpeed)
        return move, shot

